parameters.param:
@echo off
cd %teamcity.build.workingDir%

SETLOCAL
CALL packages\KoreBuild\build\dnvm use default -runtime %mr.Runtime% -arch %mr.Architecture%

if /i %mr.wwwroot%==none (
dnu publish "%mr.ProjectJson%" --configuration %mr.Configuration% --no-source --out "artifacts\%mr.ArtifactsName%"
) else (
dnu publish "%mr.ProjectJson%" --configuration %mr.Configuration% --wwwroot-out wwwroot --no-source --out "artifacts\%mr.ArtifactsName%"
)

@echo on
echo "##teamcity[publishArtifacts 'artifacts\%mr.ArtifactsName% => %mr.ArtifactsName%.zip']"
